$(function(){$("a[href^='#'], a[href^='/#']").each(function(){$(this).click(function(c){scrollTo($(this).attr("href").replace("/", ""));c.preventDefault()})});$(".mdl-layout__drawer .mdl-navigation__link").click(function(){$(".mdl-layout").get(0).MaterialLayout.toggleDrawer()});var a=$("#contact_form");var b=$("#contact_snackbar");a.submit(function(c){$.post("res/php/contact.php",a.serialize()).done(function(e){a.find(":input").attr("disabled",true);var d=a.find("button[name='submit']");d.html(d.data("success"));b.get(0).MaterialSnackbar.showSnackbar({message:e,timeout:4000})}).fail(function(d){b.get(0).MaterialSnackbar.showSnackbar({message:d.responseText,timeout:4000})});c.preventDefault()})});function scrollTo(a){$(".mdl-layout__content").scrollTo(a)};