$(function(){$("a[href^='#']").each(function(){$(this).click(function(a){scrollTo($(this).attr("href"));a.preventDefault()})});$(".mdl-layout__drawer .mdl-navigation__link").click(function(){$(".mdl-layout").get(0).MaterialLayout.toggleDrawer()});var d=$("#contact_form");var c=$("#contact_snackbar");d.submit(function(a){$.post("res/php/contact.php",d.serialize()).done(function(b){d.find(":input").attr("disabled",true);var f=d.find("button[name='submit']");f.html(f.data("success"));c.get(0).MaterialSnackbar.showSnackbar({message:b,timeout:4000})}).fail(function(b){c.get(0).MaterialSnackbar.showSnackbar({message:b.responseText,timeout:4000})});a.preventDefault()})});function scrollTo(b){$(".mdl-layout__content").scrollTo(b)};